package com.yamhto.ymId.controller;import com.yamhto.ymId.api.IdCreater;import com.yamhto.ymId.mapper.SequenceMapper;import com.yamhto.ymId.model.Sequence;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import java.util.Date;@Controllerpublic class IdController {    @Autowired    private SequenceMapper sequenceMapper;    @Autowired    private IdCreater idCreater;    @RequestMapping("id")    @ResponseBody    public String getId() {        Sequence sequence = new Sequence();        sequence.setCreateTime(new Date());        sequence.setStartValue(0L);        sequence.setStep(1L);        sequence.setValue(1L);        sequence.setSequenceType("TEST");        sequence.setVersion(1L);        return sequenceMapper.insert(sequence) + "";    }    @RequestMapping("nextId")    @ResponseBody    public String nextId() {        return idCreater.nextId("TEST",5L);    }    @RequestMapping("newPrefix")    @ResponseBody    public String newPrefix() {        return idCreater.nextId("NPC",5L);    }}